let board = {
    1: 'X', 2: 'O', 3: ' ',
    4: 'X', 5: ' ', 6: ' ',
    7: 'X', 8: ' ', 9: ' '
};

let winCombinations = [
    [1, 2, 3],[4,5,6],[7,8,9],[1,4,7],[2,5,8],[3,6,9],[1,5,9],[3,5,7]
];

let player = "X";
let combo = winCombinations[0];
//goal: match combo with player's position.


console.log(Object.keys(board)[2]) //keys at position 2 : 3
console.log(player === board[1]) //returns true. means that X is the value of key 1
console.log(board[combo[0]] === player)
console.log(combo)

function checkWin(player) {
    for (let i = 0; i < winCombinations.length; i++) {
        const combo = winCombinations[i];
        let matchCount = 0;
        for (j = 0; j < combo.length; j++) {
            if (board[combo[j]] === player) {
                matchCount++;
            }
        }
        if (matchCount >= 3) {
          return true;
        }
    }
    return false;
}

console.log(checkWin("X"))